var countDate=new Date("Jan 1, 2020 00:00").getTime();function newYear(){var e=(new Date).getTime();gap=countDate-e;var t=36e5,n=Math.floor(gap/864e5),a=Math.floor(gap%864e5/t),o=Math.floor(gap%t/6e4),r=Math.floor(gap%6e4/1e3);document.getElementById("days").innerText=n,document.getElementById("hours").innerText=a,document.getElementById("minunates").innerText=o,document.getElementById("seconds").innerText=r}setInterval(function(){newYear()},1e3);